public class Model {
	private Level level;
	private int levelID;
	private List<Level> levels;
	private Controller controller = Controller.getInstance();
	private boolean isGameComplete = false;

	public Model() {
		loadLevels();
		setLevel(levels.size());
	}

	//assumes there is at least 1 level
	private void loadLevels() {
		int levelIndex = 1;
		levels = new ArrayList<>();
		ObjectMapper mapper = new ObjectMapper();
		File file = new File("res/levels/level" + levelIndex + ".json");
		while (file.exists()) {
			levels.add(mapper.readValue(file, Level.class));
			levelIndex++;
			file = new File("res/levels/level" + levelIndex + ".json");
		}
	}
	
	// This is the heart of the game , where the model takes in all the inputs ,decides the outcomes and then changes the model accordingly. 
	public void gamelogic()
	{
		// Player Logic first 
		princessLogic();
		dragonLogic();
		levelLogic();
	}

	private void levelLogic() {
		if (level.isComplete()) {
			setLevel(levelID-1);
		}
		if (controller.isKeyPPressed()) {
			setLevel(levelID-1);
			controller.consumeKeyPPressed();
		}
	}

	private void princessLogic() {
		GameCharacter princess = level.getPrincess();
		if (princess.isMoving()) {
			princess.keepMoving();
		} else if (princess.hasMoved()) {
			princess.resetMoved();
			level.isCharacterOnButton(princess);
		} else if (princess.isFalling()) {
			princess.keepFalling();
		} else if (princess.isRespawning()) {
			princess.keepRespawning();
		} else if (princess.isBurning()) {
			princess.keepBurning();
		} else if (princess.hasBurnt()) {
			princess.resetBurnt();
			princess.respawn();
		} else if (level.princessDiedFalling()) {
			princess.fall();
		} else if (level.princessDiedBurning()) {
			princess.burn(princess.getIntPos());
		} else if (controller.isKeyWPressed()) {
			level.moveCharacter(princess, Direction.NORTH);
		} else if (controller.isKeySPressed()) {
			level.moveCharacter(princess, Direction.SOUTH);
		} else if (controller.isKeyDPressed()) {
			level.moveCharacter(princess, Direction.EAST);
		} else if (controller.isKeyQPressed()) {
			level.moveCharacter(princess, Direction.WEST);
		} else if (controller.isKeyFPressed()) {
			level.princessAction();
		}
	}

	private void dragonLogic() {
		GameCharacter dragon = level.getDragon();
		if (dragon.isMoving()) {
			dragon.keepMoving();
		} else if (dragon.hasMoved()) {
			dragon.resetMoved();
			level.isCharacterOnButton(dragon);
		} else if (dragon.isFalling()) {
			dragon.keepFalling();
		} else if (dragon.isRespawning()) {
			dragon.keepRespawning();
		} else if (level.dragonDied()) {
			dragon.fall();
		} else if (dragon.isBurning()) {
			dragon.keepBurning();
		} else if (dragon.hasBurnt()) {
			dragon.resetBurnt();
			level.burnTree();
		} else if (controller.isKeyUpPressed()) {
			level.moveCharacter(dragon, Direction.NORTH);
		} else if (controller.isKeyDownPressed()) {
			level.moveCharacter(dragon, Direction.SOUTH);
		} else if (controller.isKeyRightPressed()) {
			level.moveCharacter(dragon, Direction.EAST);
		} else if (controller.isKeyLeftPressed()) {
			level.moveCharacter(dragon, Direction.WEST);
		} else if (controller.isKeyEnterPressed()) {
			dragon.burn(TileMap.getDestination(dragon.getIntPos(), dragon.getDirection()));
		}
	}

	public int getLevelId() { return levelID; }
    public Level getLevel() { return level; }

	public void setLevel(int level) {
		this.levelID = level;
		if (levelID == 0) {
			isGameComplete = true;
		} else {
			this.level = levels.get(levelID-1);
		}

	}

	public int getNumberOfLevels() { return levels.size(); }

	public boolean isFirstLevel() { return getLevelId() == getNumberOfLevels(); }

	public boolean isGameComplete() { return isGameComplete; }
}


